document.querySelector(".mobile-menu-button").addEventListener("click",function(){document.querySelector(".mobile-menu").classList.toggle("hidden")}),document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"}),document.querySelector(".mobile-menu").classList.add("hidden")})});